import React, { useState } from 'react';
import './Chat.css'; // Optional: for styling

const Chat = () => {
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [patientId, setPatientId] = useState('');

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleSendMessage = async () => {
        if (inputValue.trim()) {
            const userMessage = inputValue;
            setMessages([...messages, userMessage]);
            setInputValue(''); // Clear the input field

            // Check if the first message is the patient-id
            if (messages.length === 0) {
                setPatientId(userMessage); // Store the patient-id
            }

            // Send the user's message to the API
            try {
                // TODO: change to the actual API endpoint
                const response = await fetch('http://localhost:1234/v1/chat/completions', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        model: "llama-3.2-3b-instruct",
                        //TODO: change to actual instructions
                        messages: [
                            { role: "system", content: "You are a large language model that is able to respond to the user's question. Your user's patient-id is {patient-id}.Your task is to do the following: 1. Provide the correct FHIR or API call as listed in the following instructions 2a. If the user is asking about a medication side effect, respond by creating an appropriate API call using the format: '!!API CALL: https://api.fda.gov/drug/label.json?search=openfda.brand_name:[medication_name]' 2b. If the user is asking about dosage, scheduling, and or administration instructions about a medication, respond by creating an appropriate API call using the format: '!!FHIR CALL: http://localhost:3000/fhir/MedicationRequest?patient=[patient-id]' 2c. If the user asks about why they are taking the medication, respond with creating an appropriate API call using the format: '!!FHIR CALL: http://localhost:3000/fhir/Condition?patient=[patient-id]' 3. If the user asks about topics that fulfill more than one criteria, respond by creating all of the appropriate API calls. 4. Do not respond with anything else aside from the API or FHIR call."},
                            { role: "user", content: userMessage }
                        ],
                        temperature: 0.5,
                        max_tokens: -1,
                        stream: false
                    }),
                });

                const data = await response.json();
                // Assuming the response contains a message from the AI
                if (data && data.choices && data.choices.length > 0) {
                    const aiMessage = data.choices[0].message.content;
                    setMessages((prevMessages) => [...prevMessages, aiMessage]);
                }
            } catch (error) {
                console.error('Error sending message:', error);
            }
        }
    };

    return (
        <div className="chat-container">
            <div className="messages">
                {messages.map((message, index) => (
                    <div key={index} className="message">
                        {message}
                    </div>
                ))}
            </div>
            <div className="input-container">
                <input
                    type="text"
                    value={inputValue}
                    onChange={handleInputChange}
                    placeholder="Type your message here..."
                />
                <button onClick={handleSendMessage}>Send</button>
            </div>
        </div>
    );
};

export default Chat;
